.video-label.top {
  top: -80px;
}

.video-label.bottom {
  bottom: -80px;
}

.tempo-icon-container {
  position: absolute;
  bottom: 30px;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 290px;
  color: white;
  z-index: 300;
}

.tempo-icon img {
  height: 40px;
  margin-bottom: 4px;
}

.tempo-icon {
  opacity: 0.6;
  display: flex;
  flex-direction: column;
  align-items: center;
  font-weight: bold;
  justify-content: center;
}

.tempo-icon-label {
  font-size: 14px;
  color: rgba(255,255,255,0.6);
}

.song-progress {
  width: 300px;
  border-radius: 20px;
  height: 25px;

  text-align: center;
  color: white;
  font-weight: bold;
  font-size: 14px;
  line-height: 25px;
}

.orchestra {
  margin-bottom: -170px;
  width: 1000px;
  margin: 0 auto;
}

.tempo-meter-container {
  position: absolute;
  padding-top: 272px;
  min-width: 1000px;
  width: 100%;
  display: flex;
  justify-content: center;
}

.tempo-meter-container-inner {
  width: 290px;
  height: 145px;
}

.tempo-meter-clipping-box {
  width: 290px;
  height: 145px;
