.tempo-meter {
  position: relative;
  width: 290px;
  height: 145px;
  z-index: 150;
  border-bottom: 10px solid #4058D3;
  border-radius: 145px 145px 0 0;
  transform-origin: bottom center;
  transform: rotateZ(-178deg);
  transition: border-color 0.5s;
}

.tempo-meter-background {
  position: absolute;
  top: 0;
  width: 290px;
  height: 145px;
  z-index: 100;
  background: #2b2984;
  border-radius: 145px 145px 0 0;
